'use strict';

var _services = require('ui_framework/services');

var _require = require('lodash'),
    memoize = _require.memoize;

var moment = require('moment');

var history = require('../history');
require('./sense_history_viewer');

require('ui/modules').get('app/sense').directive('senseHistory', function () {
  return {
    restrict: 'E',
    template: require('./history.html'),
    controllerAs: 'history',
    controller: function controller($scope, $element) {
      var _this = this;

      this.reqs = history.getHistory();
      this.selectedIndex = 0;
      this.selectedReq = this.reqs[this.selectedIndex];
      this.viewingReq = this.selectedReq;

      // calculate the text description of a request
      this.describeReq = memoize(function (req) {
        var endpoint = req.endpoint;
        var date = moment(req.time);

        var formattedDate = date.format("MMM D");
        if (date.diff(moment(), "days") > -7) {
          formattedDate = date.fromNow();
        }

        return endpoint + ' (' + formattedDate + ')';
      });
      this.describeReq.cache = new WeakMap();

      // main actions
      this.clear = function () {
        history.clearHistory($element);
        $scope.kbnTopNav.close();
      };

      this.restore = function () {
        var req = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : _this.selectedReq;

        history.restoreFromHistory(req);
        $scope.kbnTopNav.close();
      };

      this.onKeyDown = function (ev) {
        if (ev.keyCode === _services.keyCodes.ENTER) {
          _this.restore();
          return;
        }

        if (ev.keyCode === _services.keyCodes.UP) {
          ev.preventDefault();
          _this.selectedIndex--;
        } else if (ev.keyCode === _services.keyCodes.DOWN) {
          ev.preventDefault();
          _this.selectedIndex++;
        }

        _this.selectedIndex = Math.min(Math.max(0, _this.selectedIndex), _this.reqs.length - 1);
        _this.selectedReq = _this.reqs[_this.selectedIndex];
        _this.viewingReq = _this.reqs[_this.selectedIndex];
      };
    }
  };
});
