'use strict';

var _sense_editor_resize = require('../sense_editor_resize');

var SenseEditor = require('../sense_editor/editor');


require('ui/modules').get('app/sense').directive('senseHistoryViewer', function (Private) {
  var useResizeChecker = Private(_sense_editor_resize.useResizeCheckerProvider);

  return {
    restrict: 'E',
    scope: {
      req: '='
    },
    link: function link($scope, $el) {
      var viewer = new SenseEditor($el);
      viewer.setReadOnly(true);
      viewer.renderer.setShowPrintMargin(false);
      useResizeChecker($scope, $el, viewer);
      require('../settings').applyCurrentSettings(viewer);

      $scope.$watch('req', function (req) {
        if (req) {
          var s = req.method + " " + req.endpoint + "\n" + (req.data || "");
          viewer.setValue(s);
          viewer.clearSelection();
        } else {
          viewer.getSession().setValue("No history available");
        }
      });

      $scope.$on('$destroy', function () {
        viewer.destroy();
      });
    }
  };
});
