'use strict';

require('ui/elastic_textarea');

require('ui/filters/markdown');

var _modules = require('ui/modules');

var _services = require('ui_framework/services');

var _advanced_row = require('plugins/kibana/management/sections/settings/advanced_row.html');

var _advanced_row2 = _interopRequireDefault(_advanced_row);

function _interopRequireDefault(obj) { return obj && obj.__esModule ? obj : { default: obj }; }

_modules.uiModules.get('apps/management').directive('advancedRow', function (config, Notifier) {
  return {
    restrict: 'A',
    replace: true,
    template: _advanced_row2.default,
    scope: {
      conf: '=advancedRow',
      configs: '='
    },
    link: function link($scope) {
      var notify = new Notifier();

      // To allow passing form validation state back
      $scope.forms = {};

      // setup loading flag, run async op, then clear loading and editing flag (just in case)
      var loading = function loading(conf, fn) {
        conf.loading = true;
        fn().then(function () {
          conf.loading = conf.editing = false;
        }).catch(notify.fatal);
      };

      $scope.maybeCancel = function ($event, conf) {
        if ($event.keyCode === _services.keyCodes.ESCAPE) {
          $scope.cancelEdit(conf);
        }
      };

      $scope.edit = function (conf) {
        conf.unsavedValue = conf.value == null ? conf.defVal : conf.value;
        $scope.configs.forEach(function (c) {
          c.editing = c === conf;
        });
      };

      $scope.save = function (conf) {
        loading(conf, function () {
          if (conf.unsavedValue === conf.defVal) {
            return config.remove(conf.name);
          }

          return config.set(conf.name, conf.unsavedValue);
        });
      };

      $scope.cancelEdit = function (conf) {
        conf.editing = false;
      };

      $scope.clear = function (conf) {
        return loading(conf, function () {
          return config.remove(conf.name);
        });
      };

      $scope.isDefaultValue = function (conf) {
        // conf.isCustom = custom setting, provided by user, so there is no notion of
        // having a default or non-default value for it
        return conf.isCustom || conf.value === undefined || conf.value === '' || String(conf.value) === String(conf.defVal);
      };
    }
  };
});
